import { useNavigate } from "react-router-dom";
import { useState } from "react";
import 'boxicons'


const LogIn = (props) => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [authToken, setAuthToken] = useState("");
    const navigate = useNavigate();

    const EmailHandler = (e) => {
        setEmail(e.target.value);
    }

    const PasswordHandler = (e) => {
        setPassword(e.target.value);
    }

    const PasswordCheck = () => {
        if (email.length > 1 && password.length > 1) {
            // send to ewa
            // set route to /home
            navigate("/home");
            setAuthToken("");


            props.onToken(authToken);
        }
    }

    return (
        <>
            <h1>Login</h1>
            <form>
                <label htmlFor="email">Email: </label>
                <input id="email" type="text" onChange={EmailHandler}></input>
                <br/>
                <label htmlFor="password">Password: </label>
                <div className="password_con">
                    <input id="password" type="text" className="password_input" onChange={PasswordHandler}></input>
                    <box-icon name='hide'></box-icon>
                </div>
                <br/>
                <button type="button" className="login_btn" onClick={PasswordCheck}>Login</button>

            </form>
        </>
    )
}

export default LogIn;
