import { Link, NavLink } from "react-router-dom";
import { useState } from "react";
import {useHistory} from "react-router-dom"

const LogIn = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const history = useHistory();

    const EmailHandler = (e) => {
        setEmail(e.target.value);
    }

    const PasswordHandler = (e) => {
        setPassword(e.target.value);
    }

    const PasswordCheck = () => {
        // send to ewa
        console.log("checking")
        // set route to /home
        history.push("/home");
    }

    return (
        <>
            <h1>Login</h1>
            <form>
                <label htmlFor="email">Email: </label>
                <input id="email" type="text" onChange={EmailHandler}></input>
                <br/>
                <label htmlFor="password">Password: </label>
                <input id="password" type="text" onChange={PasswordHandler}></input>
                <br/>
                <button className="login_btn" onClick={email.length > 1 && password.length > 1 && PasswordCheck}>Login</button>
            </form>
        </>
    )
}

export default LogIn;
